def GCD_modulo(a,b):
    while(b != 0):
        c = a % b
        a = b
        b = c

    return a

def GCD_modulo_extended(a,b):
    x = 1
    y = 0
    r = 0
    s = 1

    while(b != 0):
        c = a % b
        q = a // b
        a = b
        b = c

        r_prim = r
        s_prim = s
        r = x - q * r
        s = y - q * s
        x = r_prim
        y = s_prim

    return (a, x, y)



